import fs from 'fs';
import pdf from '@cyber2024/pdf-parse-fixed';
import axios from 'axios'

export default async function pdfToText({ pdfPath, pdfUrl }: NodeInputs, { logging }: NodeScriptOptions) : NodeOutput  {
  if (!pdfPath && !pdfUrl) throw Error("You must specify either a pdf path or pdf url.")

  let dataBuffer;
  if (pdfUrl) {
    const response = await axios.get(pdfUrl, {
      responseType: 'arraybuffer'
    });
    dataBuffer = response.data
  } else {
    const localPath = process.env.BUCKET_FOLDER_PATH + pdfPath
    dataBuffer = fs.readFileSync(localPath);
  }
  const data = await pdf(dataBuffer)
  return data.text
}

