const getAccessToken = async () => {
  const response = await fetch(
    "http://metadata/computeMetadata/v1/instance/service-accounts/default/token",
    { headers: { "Metadata-Flavor": "Google" } }
  );
  if (!response.ok) {
    throw new Error(`Failed to obtain access token: ${response.statusText}`);
  }
  const data: any = await response.json();
  return data?.access_token;
};

export default async function openaiGptTextGenerator(
  {
    userPrompt,
    systemPrompt,
    maxTokens,
    temperature,
    model,
    response_schema,
    kbIntegrationKey,
  }: NodeInputs & { kbIntegrationKey: string },
  { auth, workflow, node }: NodeScriptOptions
): NodeOutput {
  const hasCredits = kbIntegrationKey?.split(";;")[1] === "credits";
  const apiKey = hasCredits ? await getAccessToken() : auth.getKey();

  if (!apiKey) {
    throw new Error(
      "An integration key is required for this node to function, but no key was selected. Please select one from the node's toolbar."
    );
  }

  const client = new OpenAI({
    apiKey,
    ...(hasCredits && { baseURL: "https://proxy.buildship.run/llm/openai" }),
  });


  const response = await client.responses.create(
    {
      model,
      input: [
        {
          role: "system",
          content: systemPrompt,
        },
        {
          role: "user",
          content: userPrompt,
        },
      ],
      text: {
        format: transformResponseSchema(response_schema),
      },
      max_output_tokens: maxTokens,
      temperature,
    },
    hasCredits
      ? {
          headers: {
            "x-buildship-workflow-id": workflow?.id,
            "x-buildship-node-id": node?.id,
          },
        }
      : undefined
  );

  try {
    return JSON.parse(response.output_text);
  } catch (e) {
    throw new Error("Failed to parse response output_text as JSON.");
  }
}


function transformResponseSchema(response_schema: any) {
  if (!response_schema?.json_schema?.schema) {
    throw new Error("Invalid or missing response_schema format.");
  }

  const UNSUPPORTED_BY_TYPE: Record<string, string[]> = {
    string: ["minLength", "maxLength", "pattern", "format"],
    number: ["minimum", "maximum", "multipleOf"],
    object: [
      "patternProperties", "unevaluatedProperties", "propertyNames",
      "minProperties", "maxProperties"
    ],
    array: [
      "unevaluatedItems", "contains", "minContains", "maxContains",
      "minItems", "maxItems", "uniqueItems"
    ],
  };

  const clean = (schema: any, inProps = false): any => {
    if (Array.isArray(schema)) return schema.map((s) => clean(s, inProps));
    if (typeof schema !== "object" || schema === null) return schema;

    const type = schema.type;
    const unsupported = inProps ? [] : (UNSUPPORTED_BY_TYPE[type] || []);

    return Object.fromEntries(
      Object.entries(schema).map(([k, v]) => {
        if (unsupported.includes(k)) return null;
        const nested = k === "properties" ? clean(v, true) : clean(v, false);
        return [k, nested];
      }).filter(Boolean)
    );
  };

  return {
    type: "json_schema",
    name: response_schema.json_schema.name || "response",
    strict: true,
    schema: clean(response_schema.json_schema.schema),
  };
}

