        previousMessages.push({ role: "user", content: userRequest });
        previousMessages.push({
          role: "assistant",
          content: assistantResponse.content || assistantResponse,
        });
      }
    }

    const jsonInstruction = "##Output a valid JSON Object. Provide detailed answers with inline citations using markdown links. Example: [Source](https://example.com)##.\n";

    const messages = [
      {
        role: "system",
        content: outputFormat === "json_object"
          ? jsonInstruction + systemPrompt
          : systemPrompt ?? "Provide detailed answers with inline citations using markdown links. Example: [Source](https://example.com)",
      },
      ...previousMessages,
      { 
        role: "user", 
        content: userRequest || "Default user prompt"
      },
    ];

    messages.forEach(msg => {
      if (typeof msg.content !== 'string') {
        msg.content = JSON.stringify(msg.content);
      }
    });

    const response = await client.chat.completions.create(
      {
        model,
        temperature,
        messages,
        max_tokens: maxTokens,
        ...(outputFormat === "json_object" && {
          response_format: { type: "json_object" },
        }),
      },
      {
        ...(kbIntegrationKey?.split(";;")[1] === "credits"
          ? {
              headers: {
                "x-buildship-workflow-id": workflow?.id,
                "x-buildship-node-id": node?.id,
              },
            }
          : {}),
      },
    );

    const processedOutput = processResponse(response, outputFormat);

    if (sessionKey) {
      const folderPath = path.dirname(filePath);
      if (!fs.existsSync(folderPath)) {
        fs.mkdirSync(folderPath, { recursive: true });
      }
      const dataToSave = { userRequest, assistantResponse: processedOutput };
      fs.appendFileSync(filePath, JSON.stringify(dataToSave) + "\n");
    }

    return processedOutput;
  } catch (e) {
    console.error("Perplexity API Error:", e);
    throw new Error(`Perplexity API Error: ${e.message}`);
  }
