 * @param {Object} response - The response from Perplexity AI
 * @param {String} outputFormat - The desired output format
 * @returns {Object} - Processed response with citations, content, and reasoning
 */
function processResponse(response, outputFormat) {
  // Extract content from the message
  const messageContent = response.choices[0].message.content || "";

  // Extract citations if they exist in the response
  const citations = response.citations || [];

  // Extract reasoning if it exists within <think>...</think> tags
  let reasoning = "";
  const thinkMatch = messageContent.match(/<think>([\s\S]*?)<\/think>/);
  if (thinkMatch && thinkMatch[1]) {
    reasoning = thinkMatch[1].trim();
  }

  // Extract the actual content (everything after the </think> tag or the whole content if no think tag)
  let content = messageContent;
  if (thinkMatch) {
    content = messageContent.replace(/<think>[\s\S]*?<\/think>\s*/, "").trim();
  }

  // Handle citation references in content (e.g., [1], [2], etc.)
  // Remove citation references from the content
  content = content.replace(/\[\d+\]/g, "").trim();

  // Parse JSON content if needed
  let parsedContent = content;
  if (outputFormat === "json_object") {
    try {
      parsedContent = JSON.parse(content || "{}");
    } catch (e) {
      console.warn("Failed to parse JSON response:", e.message);
      parsedContent = { error: "Invalid JSON response", raw: content };
    }
  }

  // Prepare the final output based on the desired format
  const result = {
    citations: citations.reduce((acc, url, index) => {
      acc[index + 1] = url;
      return acc;
    }, {}),
    content: outputFormat === "json_object" ? parsedContent : content,
    reasoning: reasoning || undefined
  };

  // If reasoning is empty, don't include it in the output
  if (!reasoning) {
    delete result.reasoning;
  }

  return result;
}

