export default async function perplexityAIChatModel(
  {
    userRequest,
    systemPrompt,
    model,
    temperature,
    sessionKey,
    conversationLimit,
    maxTokens,
    outputFormat,
    kbIntegrationKey,
  },
  { logging, auth, workflow, node },
) {
  try {
    const client =
      kbIntegrationKey?.split(";;")[1] === "credits"
        ? new OpenAI({
            baseURL: "https://proxy.buildship.run/llm/perplexity",
            apiKey: await getAccessToken(),
          })
        : new OpenAI({
            apiKey: auth.getKey(),
            baseURL: "https://api.perplexity.ai"
          });

    let previousMessages = [];
    const filePath =
      process.env.BUCKET_FOLDER_PATH +
      "/nodes/openai-chat/store/" +
      sessionKey +
      ".jsonl";

    if (sessionKey && fs.existsSync(filePath)) {
      const fileContent = fs.readFileSync(filePath, "utf-8");
      const lines = fileContent.trim().split("\n").slice(-conversationLimit);
      for (const line of lines) {
        const { userRequest, assistantResponse } = JSON.parse(line);
        previousMessages.push({ role: "user", content: userRequest });
        previousMessages.push({
          role: "assistant",
          content: assistantResponse.content || assistantResponse,
        });
      }
    }

    const jsonInstruction = "##Output a valid JSON Object. Provide detailed answers with inline citations using markdown links. Example: [Source](https://example.com)##.\n";

    const messages = [
      {
        role: "system",
        content: outputFormat === "json_object"
          ? jsonInstruction + systemPrompt
          : systemPrompt ?? "Provide detailed answers with inline citations using markdown links. Example: [Source](https://example.com)",
      },
      ...previousMessages,
      { 
        role: "user", 
        content: userRequest || "Default user prompt"
      },
    ];

    messages.forEach(msg => {
      if (typeof msg.content !== 'string') {
        msg.content = JSON.stringify(msg.content);
      }
    });

    const response = await client.chat.completions.create(
      {
        model,
        temperature,
        messages,
        max_tokens: maxTokens,
        ...(outputFormat === "json_object" && {
          response_format: { type: "json_object" },
        }),
      },
      {
        ...(kbIntegrationKey?.split(";;")[1] === "credits"
          ? {
              headers: {
                "x-buildship-workflow-id": workflow?.id,
                "x-buildship-node-id": node?.id,
              },
            }
          : {}),
      },
    );

    const processedOutput = processResponse(response, outputFormat);

    if (sessionKey) {
      const folderPath = path.dirname(filePath);
      if (!fs.existsSync(folderPath)) {
        fs.mkdirSync(folderPath, { recursive: true });
      }
      const dataToSave = { userRequest, assistantResponse: processedOutput };
      fs.appendFileSync(filePath, JSON.stringify(dataToSave) + "\n");
    }

    return processedOutput;
  } catch (e) {
    console.error("Perplexity API Error:", e);
    throw new Error(`Perplexity API Error: ${e.message}`);
  }
}

/**
 * Process the response from Perplexity AI API
 * @param {Object} response - The response from Perplexity AI
 * @param {String} outputFormat - The desired output format
 * @returns {Object} - Processed response with citations, content, and reasoning
 */
function processResponse(response, outputFormat) {
  // Extract content from the message
  const messageContent = response.choices[0].message.content || "";

  // Extract citations if they exist in the response
  const citations = response.citations || [];

  // Extract reasoning if it exists within <think>...</think> tags
  let reasoning = "";
  const thinkMatch = messageContent.match(/<think>([\s\S]*?)<\/think>/);
  if (thinkMatch && thinkMatch[1]) {
    reasoning = thinkMatch[1].trim();
  }

  // Extract the actual content (everything after the </think> tag or the whole content if no think tag)
  let content = messageContent;
  if (thinkMatch) {
    content = messageContent.replace(/<think>[\s\S]*?<\/think>\s*/, "").trim();
  }

  // Handle citation references in content (e.g., [1], [2], etc.)
  // Remove citation references from the content
  content = content.replace(/\[\d+\]/g, "").trim();

  // Parse JSON content if needed
  let parsedContent = content;
  if (outputFormat === "json_object") {
    try {
      parsedContent = JSON.parse(content || "{}");
    } catch (e) {
      console.warn("Failed to parse JSON response:", e.message);
      parsedContent = { error: "Invalid JSON response", raw: content };
    }
  }

  // Prepare the final output based on the desired format
  const result = {
    citations: citations.reduce((acc, url, index) => {
      acc[index + 1] = url;
      return acc;
    }, {}),
    content: outputFormat === "json_object" ? parsedContent : content,
    reasoning: reasoning || undefined
  };

  // If reasoning is empty, don't include it in the output
  if (!reasoning) {
    delete result.reasoning;
  }

  return result;
}

