import parser from "co-body";

const onExecution = async (
	{ method, requestContentType },
	{ nodeReq, request, logging }
) => {
	let body;
	if (method !== "GET") {
        const limit = process.env?.PROJECT_PLAN === "FREE" ? "1MB" : "32MB";
		//const parser = require('co-body');
		//const parser = await import('co-body');
		switch (requestContentType) {
			case "text/plain":
				body = await parser.text(nodeReq, { limit });
				break;
			case "application/x-www-form-urlencoded":
				body = await parser.form(nodeReq, { limit });
				break;
			case "application/json":
				body = await parser.json(nodeReq, { limit });
				break;
			default:
				break;
		}
	}
	const ret = {
		query: request.query,
		headers: request.headers,
		body: body !== null && body !== void 0 ? body : {},
        params: request.params,
        path: request.path
	};
	return ret;
};

const getAccessToken = async () => {
	const response = await fetch(
		"http://metadata/computeMetadata/v1/instance/service-accounts/default/token",
		{
			headers: { "Metadata-Flavor": "Google" },
		}
	);

	if (!response.ok) {
		throw new Error(
			`Failed to obtain access token: ${response.statusText}`
		);
	}

	const data = await response.json();
	return data.access_token;
};

const fetchLogEntries = async (triggerId, retries = 3, delay = 1000) => {
	const projectId = process.env.GCLOUD_PROJECT;
	const accessToken = await getAccessToken();

	const filter = `logName="projects/${projectId}/logs/buildship-node-io" AND jsonPayload.nId="${triggerId}"`;

	const requestBody = {
		resourceNames: [`projects/${projectId}`], // Added resourceNames field
		filter: filter,
		pageSize: 1,
		orderBy: "timestamp desc",
	};

	for (let attempt = 1; attempt <= retries; attempt++) {
		try {
			const response = await fetch(
				"https://logging.googleapis.com/v2/entries:list",
				{
					method: "POST",
					headers: {
						Authorization: `Bearer ${accessToken}`,
						"Content-Type": "application/json",
					},
					body: JSON.stringify(requestBody),
				}
			);

			if (!response.ok) {
				const errorText = await response.text();
				throw new Error(
					`Error fetching log entries: ${response.statusText} - ${errorText}`
				);
			}

			const data = await response.json();
			const entries = data.entries || [];

			if (entries.length > 0) {
				return entries;
			}

			// Delay before the next attempt if entries were empty
			if (attempt < retries) {
				await new Promise((resolve) => setTimeout(resolve, delay));
			}
		} catch (error) {
			console.error(`Attempt ${attempt} failed due to an error:`, error);
			// Retry if it's not the last attempt
			if (attempt < retries) {
				await new Promise((resolve) => setTimeout(resolve, delay));
			} else {
				throw new Error(
					`Failed to get data after ${retries} attempts.`
				);
			}
		}
	}

	// If all retries exhausted and no entries found
	throw new Error("No data found. Send a request to the API and try again.");
};

const getData = async (inputs, { trigger, workflow }) => {
	try {
		const entries = await fetchLogEntries(trigger.id, 5, 2000);
		return { success: true, message: "", data: entries[0].jsonPayload.o };
	} catch (err) {
		return { success: false, message: err?.message, data: null };
	}
};

export default { onExecution, onCreate: () => {}, onUpdate: () => {}, getData };
